(window.webpackJsonp=window.webpackJsonp||[]).push([[19],{429:function(t,n,e){"use strict";e.r(n),n.default={index:{header:{navigation:["Главная","Как это работает","Личный кабинет","Калькулятор доходности","О продукте","F.A.Q"],btn:"Начать",singInBtn:"Вход",singUpBtn:"Регистрация"},hero:{title:"Платформа для копирования сделок",list:["Автоматическое копирование <b>ЛЮБЫХ</b>\nваших сделок на аккаунты инвесторов","Умная система автоматического расчета\nприбыли между трейдером и инвестором","Подключение через API\nс доступом только на чтение сделок"],btn_1:"Создать аккаунт",btn_2:"Войти"},advantages:{title:"Наши преимущества",list:[{title:"Удобство",text:"Подключите один раз\nAPI-ключи своего аккаунта\nи продолжайте торговлю.\nВаши сделки будут\nавтоматически скопированы\nна аккаунты инвесторов"},{title:"Безопасность",text:"Все активыs\nнаходятся исключительно\nна личных счетах\nтрейдеров и инвесторов,\nбез возможности передачи\nдоступа и вывода средств"},{title:"Интерфейс",text:"Удобный\nличный кабинет\nдля управления капиталом\nс подробной аналитикой\nпо всем вашим инвесторам,\nсделкам и позициям"},{title:"Открытость",text:"При необходимости\nвы всегда\nможете связаться\nс разработчиками напрямую.\nЭто уникальная возможность,\nдоступная немногим."}]},request:{text:"Протестируйте нашу платформу прямо сейчас - это абсолютно бесплатно!",btn:"Протестировать"},functionality:{title:"Как это работает",text:"Торгуйте только на вашем основном биржевом аккаунте, алгоритм <b>APIX</b> будет копировать все ваши сделки \nна аккаунты ваших инвесторов за 0.22 секунда 24/7",sub_title:"Полная автоматизация процессов",list:["Платформа <b>APIX TRADE</b> копирует абсолютно \nвсе функции которые воспроизводит трейдер \nна воём биржевом аккаунте <b>BINANCE FUTURES</b> \nи в точности повторяет результат \nна аккаунтах инвесторов.","После успешного завершения сделок платформа \n<b>автоматически</b> производит расчёт и по запросу \nтрейдера выставит счета на оплату всем инвесторам! \nСумму вашей комиссии от прибыли инвесторов \nвы сможете указать самостоятельно при регистрации \nаккаунта в <b>APIX</b>. ","Ваш аккаунт остаётся <b>под вашим контролем</b>, \nдля начала работы нам потребуются ваши ключи \nтолько с правами на чтение фьючерсов."]},stats:{traiders:"Подключено Трейдеров",investors:"Подключено Инвесторов",capital:"Капитал в Управлении",income:"Прибыль трейдеров",description:"Обновляется 1 раз в месяц"},cabinet:{title:"Личный кабинет",text:"Удобный и интуитивно понятный интерфейс имеет всё необходимое для быстрой и эффективной работы.<br>Вся статистика вашего фонда ДУ в одном месте, как на ладони! \n",list:[{title:"Управление капиталом",text:"Отслеживайте общую сумму подключенного к вам капитала,\nкоторая находится у вас в управлении!"},{title:"Статистика инвестора",text:"Отслеживайте текущий биржевой баланс каждого\nвашего инвестора, а также контактыи статус подключения"},{title:"Рассчет прибыли",text:"Автоматический расчет прибыли после завершения сделок, \nвыставления счетов инвесторам по вашему запросу"},{title:"Общая статистика",text:"Подробная статистика по вашим сделкам, позициям, \nисториям сделок и всем счетам инвесторов за все время!"}]},calc:{title:"Калькулятор доходности",capital:"Ожидаемый капитал в управлении",average_income:"Ваш средний % прибыли в месяц",commission:"Ваша коммиссия от прибыли",income:"Ваша прибыль",day:"В день",month:"В месяц",year:"В год"},aboutProduct:{title:"О продукте",text:"APIX — это новая парадигма в доверительном управлении активами: отсутствие скрытых комиссий, никаких третьих лиц. \nВы всегда имеете полный контроль над своим счетом, 24/7.\n"},registrationOffer:{text:"Начни зарабатывать дополнительные деньги от своего трейдинга прямо сейчас!\nОставь заявку на получение DEMO-доступа и протестируй функционал нашей платформы",btn:"Зарегистрироваться"},stepsOfWorkWithSystem:{title:"Три простых шага<br>для работы с APIX.TRADE",steps:[{title:"Заявка",description:"Вы оставляете заявку\nна подключение аккаунта трейдера\nна платформе APIX.trade"},{title:"Регистрация",description:"В течении нескольких часов\nс вами свяжется наш менеджер\nи предоставит доступ к аккаунту"},{title:"Подключение инвесторов",description:"Ваш аккаунт готов,\nначинайте подключение инвесторов\nпо пригласительной ссылке"}]},faq:{title:"Популярные вопросы",list:[{groupName:"Для трейдеров",items:[{title:"Быстрый старт",text:"Зарегистрируйтесь на APIX.TRADE, введите свои api-ключи, отправьте ссылку своим инвесторам и зарабатывайте 40% от их прибыли."},{title:"Основные правила",text:"Трейдеру и инвестору запрещается пополнять и выводить средства с баланса при открытых позициях.\nМинимальный баланс трейдера должен быть не менее 1000$"},{title:"Подключение инвесторов",text:"В момент когда вы планируете подключение новых инвесторов, не держите открытых позиций, иначе новые инвесторы будут пропускать\nваши сделки по этой паре до тех пор, пока вы не закроете позицию и не откроете ее заново. Это правило не распространяется на новые позиции!"},{title:"Распределение прибыли/расчет",text:"Расчет прибыли происходит каждые 2 недели; инвестор при подключении вносит 10% от суммы своего стартового депозита на внутренний баланс\nв APIX TRADE для покрытия комиссии трейдеру от будущей прибыли. После расчета вы можете вывести вашу прибыль в любой момент."}]},{groupName:"Для инвесторов",items:[{title:"Во сколько мне обойдутся услуги копи-трейдинга?",text:"Согласно правилам платформы APIX, 40% забирает себе трейдер и 10% идут на оплату комиссий сервиса."},{title:"Как мне воспользоваться услугами нескольких трейдеров сразу?",text:"К аккаунту биржи можно подключить только одного трейдера.\nДля добавления большего количества экспертов необходимо добавить дополнительный аккаунт Binance."},{title:"Существует ли задержка во время копирования сделок?",text:"Мы знаем, насколько важна скорость копирования ордеров, поэтому разработали самую быструю систему копирования сделок.\nПри копировании система активируется мгновенно. Средняя задержка при копировании заказа —  0,2 секунды."},{title:"Как мне оплатить услуги APIX.TRADE?",text:"Оплатить услуги трейдеров и платформы Вы можете через кошелек APIX — внутреннего кошелька платформы APIX.TRADE,\nиспользуемого для оплаты услуг и продуктов экосистемы сервиса. "},{title:"Как мне вывести мою прибыль?",text:"Вы можете увидеть Ваш доход на биржевом счете. Способы, условия и сроки вывода определяются биржей,\nна которой у Вас открыт счет. APIX получает разрешение только на отправку ордеров."}]}]},start:{title:"Остались вопросы о APIX.TRADE?",description:"Если у вас остались вопросы связанные с функционалом нашей платформе, не стесняйтесь\nзадавайте их нашей команде. Мы всегда готовы ответить на них.",btn:"Написать Менеджеру"}}}}}]);